chargenPDF.prototype.checkLineCol = function (lineN, colC) {

	if( this.portraitCalc ) {
		addPage = false;
		if( lineN > 300)  {
			lineN = 20;
			//colC = 155;
			addPage = true;
		}
	} else {

		addPage = false;
		if( lineN > 200)  {
			lineN = 20;
			colC = 155;

		}
	}
	return [lineN, colC, addPage];
}

